// src/components/StudentRecords.js
import React, { useState, useEffect } from 'react';
import { Container, Typography, Button, TextField, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Box } from '@mui/material';
import axios from 'axios';

const StudentRecords = () => {
  const [students, setStudents] = useState([]);
  const [newStudent, setNewStudent] = useState({ name: '', registrationNo: '', contact: '' });

  useEffect(() => {
    // Fetch students from the backend
    axios.get('http://localhost:8000/api/students')
      .then(response => setStudents(response.data))
      .catch(err => console.error(err));
  }, []);

  const handleAddStudent = async () => {
    try {
      const response = await axios.post('http://localhost:8000/api/students', newStudent);
      setStudents([...students, response.data]); // Update the list with new student
      setNewStudent({ name: '', registrationNo: '', contact: '' });
    } catch (err) {
      console.error(err);
    }
  };

  const handleDeleteStudent = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/api/students/${id}`);
      setStudents(students.filter(student => student.id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Container maxWidth="md">
      <Typography variant="h4" align="center" sx={{ mt: 4 }}>Student Records</Typography>

      {/* Student Form */}
      <Box sx={{ mt: 4, p: 2, boxShadow: 2 }}>
        <Typography variant="h6">Register New Student</Typography>
        <TextField 
          label="Name" 
          value={newStudent.name} 
          onChange={(e) => setNewStudent({ ...newStudent, name: e.target.value })} 
          fullWidth margin="normal" 
        />
        <TextField 
          label="Registration Number" 
          value={newStudent.registrationNo} 
          onChange={(e) => setNewStudent({ ...newStudent, registrationNo: e.target.value })} 
          fullWidth margin="normal" 
        />
        <TextField 
          label="Contact" 
          value={newStudent.contact} 
          onChange={(e) => setNewStudent({ ...newStudent, contact: e.target.value })} 
          fullWidth margin="normal" 
        />
        <Button variant="contained" color="primary" fullWidth onClick={handleAddStudent}>
          Add Student
        </Button>
      </Box>

      {/* Student List */}
      <TableContainer component={Paper} sx={{ mt: 4 }}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Name</TableCell>
              <TableCell>Registration Number</TableCell>
              <TableCell>Contact</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {students.map((student) => (
              <TableRow key={student.id}>
                <TableCell>{student.name}</TableCell>
                <TableCell>{student.registrationNo}</TableCell>
                <TableCell>{student.contact}</TableCell>
                <TableCell>
                  <Button variant="outlined" color="secondary" onClick={() => handleDeleteStudent(student.id)}>
                    Delete
                  </Button>
                  {/* You can add an update functionality here */}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Container>
  );
};

export default StudentRecords;
